DROP TABLE IF EXISTS LISTENNING;
DROP TABLE IF EXISTS VIDEO;
DROP TABLE IF EXISTS RESOURCE;
DROP TABLE IF EXISTS LEVEL;
DROP TABLE IF EXISTS SOURCE;

CREATE TABLE SOURCE (
id_source smallint PRIMARY KEY,
source VARCHAR(50) NOT NULL
);

CREATE TABLE LEVEL (
id_level smallint PRIMARY KEY,
level VARCHAR(50) NOT NULL
);

CREATE TABLE RESOURCE (
id_resource smallint PRIMARY KEY,
resource VARCHAR(255) NOT NULL,
url VARCHAR(255) NOT NULL,
description VARCHAR(255) NOT NULL,
comments LONGTEXT NULL,
id_level smallint NOT NULL,
active BOOLEAN NOT NULL,
has_script BOOLEAN NOT NULL,
has_grammar BOOLEAN NOT NULL,
has_quiz BOOLEAN NOT NULL,
has_vocabulary BOOLEAN NOT NULL,
id_source smallint NOT NULL,
FOREIGN KEY (id_level) REFERENCES LEVEL(id_level),
FOREIGN KEY (id_source) REFERENCES SOURCE(id_source)
);

CREATE TABLE VIDEO (
id_video smallint PRIMARY KEY,
id_resource smallint NOT NULL,
FOREIGN KEY (id_resource) REFERENCES RESOURCE(id_resource)
);

CREATE TABLE LISTENNING (
id_listenning smallint PRIMARY KEY,
id_resource smallint NOT NULL,
FOREIGN KEY (id_resource) REFERENCES RESOURCE(id_resource)
);







