INSERT INTO RESOURCE(id_resource, resource, url, description, comments, id_level, active,has_script,has_grammar,has_quiz,has_vocabulary, id_source, id_skill) VALUES(390,'Video games are good for you!','https://learnenglishteens.britishcouncil.org/skills/reading/upper-intermediate-b2-reading/video-games-are-good-you','You know all those people that told you that video games are bad for you? They were wrong. Read this to find out more.',NULL,6,true,true,false,true,false,2,2);
